# Discord Client: Used by the Genesis module to get information. import logging from fastapi import FastAPI, HTTPException import requests from pydantic import BaseModel app = FastAPI(title="Discord Client", version="3.0") logger = logging.getLogger("DiscordClient") class MessageRequest(BaseModel): message: str @app.post("/send_message") def send_message(req: MessageRequest): # Send the message to the Discord channel result = {"status": "sent", "message": req.message} # This is a placeholder for the actual message sending logic return result @app.get("/health") def health(): return {"status": "healthy"} if __name__ == "__main__": import uvicorn uvicorn.run(app, host="0.0.0.0", port=8003)